#!/usr/bin/env node

try {
  require('../lib/cli.js')(process)
} catch (error) {
  console.error('Error loading npm:', error.message);
  
  if (error.code === 'MODULE_NOT_FOUND') {
    console.error('\nNPM installation may be incomplete. Try these steps:');
    console.error('1. Run Command Prompt as Administrator');
    console.error('2. Run: npm install -g npm');
    console.error('3. Or reinstall Node.js from https://nodejs.org/\n');
  }
  
  process.exit(1);
}
